import os
import sys

def process_data(items, results=[]):
    """Process data items."""
    for item in items:
        if item>10:
            if item>20:
                if item>30:
                    results.append(item*2)
                else:
                    results.append(item)
            else:
                results.append(item/2)
        else:
            results.append(0)
    return results

